  od"

definition
copy_global_mappings :: "obj_ref \<Rightarrow> (unit,'z::state_ext) s_monad" where
  "copy_global_mappings new_pm \<equiv> do
    global_pm \<leftarrow> gets (x64_global_pml4 \<circ> arch_state);
    base \<leftarrow> return $ get_pml4_index pptr_base;
    pme_bits \<leftarrow> return word_size_bits;
    pm_size \<leftarrow> return (1 << ptTranslationBits);
    mapM_x (\<lambda>index. do
        offset \<leftarrow> return (index << pme_bits);
        pme \<leftarrow> get_pml4e (global_pm + offset);
        store_pml4e (new_pm + offset) pme
    od) [base  .e.  pm_size - 1]
  od"

definition
lookup_pd_slot :: "obj_ref \<Rightarrow> vspace_ref \<Rightarrow> (obj_ref,'z::state_ext) lf_monad" where
"lookup_pd_slot pd vptr \<equiv> doE
    pdpt_slot \<leftarrow> lookup_pdpt_slot pd vptr;
    pdpte \<leftarrow> liftE $ get_pdpte pdpt_slot;
    (case pdpte of
          PageDirectoryPDPTE tab _ _ \<Rightarrow> (doE
            pd \<leftarrow> returnOk (ptrFromPAddr tab);
            pd_index \<leftarrow> returnOk (get_pd_index vptr);
            pd_slot \<leftarrow> returnOk (pd + (pd_index << word_size_bits));
            returnOk pd_slot
          odE)
        | _ \<Rightarrow> throwError $ MissingCapability pdpt_shift_bits)
 odE"

definition
lookup_pt_slot :: "obj_ref \<Rightarrow> vspace_ref \<Rightarrow> (obj_ref,'z::state_ext) lf_monad" where
"lookup_pt_slot vspace vptr \<equiv> doE
    pd_slot \<leftarrow> lookup_pd_slot vspace vptr;
    pde \<leftarrow> liftE $ get_pde pd_slot;
    (case pde of
          PageTablePDE ptab _ _ \<Rightarrow>   (doE
            pt \<leftarrow> returnOk (ptrFromPAddr ptab);
            pt_index \<leftarrow> returnOk (get_pt_index vptr);
            pt_slot \<leftarrow> returnOk (pt + (pt_index << word_size_bits));
            returnOk pt_slot
          odE)
        | _ \<Rightarrow> throwError $ MissingCapability pd_shift_bits)
   odE"

text \<open>This definition decodes architecture-specific invocations.
\<close>

definition
  page_base :: "vspace_ref \<Rightarrow> vmpage_size \<Rightarrow> vspace_ref"
where
  "page_base vaddr vmsize \<equiv> vaddr && ~~ mask (pageBitsForSize vmsize)"



*)

definition
decode_page_invocation :: "data \<Rightarrow> data list \<Rightarrow> cslot_ptr \<Rightarrow> arch_cap
                            \<Rightarrow> (cap \<times> cslot_ptr) list \<Rightarrow> (arch_invocation,'z::state_ext) se_monad"
where
  "decode_page_invocation label args cte cap extra_caps \<equiv> (case cap of
  PageCap dev p R map_type pgsz mapped_address \<Rightarrow>
    if invocation_type label = ArchInvocationLabel X64PageMap then
    if length args > 2 \<and> length extra_caps > 0
    then let vaddr = args ! 0;
             rights_mask = args ! 1;
             attr = args ! 2;
             vspace_cap = fst (extra_caps ! 0)
         in doE
             (vspace, asid) \<leftarrow> (case vspace_cap of
                                   ArchObjectCap (PML4Cap pm (Some asid)) \<Rightarrow>
                                         returnOk (pm, asid)
                                 | _ \<Rightarrow> throwError $ InvalidCapability 1);
             case mapped_address of
               Some (asid', vaddr') \<Rightarrow> doE
                 whenE (asid' \<noteq> asid) (throwError $ InvalidCapability 1);
                 whenE (map_type \<noteq> VMVSpaceMap) $ throwError IllegalOperation;
                 whenE (vaddr' \<noteq> vaddr) (throwError $ InvalidArgument 0)
               odE
             | None \<Rightarrow> doE
                 vtop \<leftarrow> returnOk $ vaddr + bit (pageBitsForSize pgsz);
                 whenE (vaddr > user_vtop \<or> vtop > user_vtop) $ throwError $ InvalidArgument 0
               odE;
             vspace' \<leftarrow> lookup_error_on_failure False $ find_vspace_for_asid asid;
             whenE (vspace' \<noteq> vspace) $ throwError $ InvalidCapability 1;
             vm_rights \<leftarrow> returnOk $ mask_vm_rights R $ data_to_rights rights_mask;
             check_vp_alignment pgsz vaddr;
             entries \<leftarrow> create_mapping_entries (addrFromPPtr p) vaddr pgsz vm_rights
                                               (attribs_from_word attr) vspace;
             ensure_safe_mapping entries;
             returnOk $ InvokePage $ PageMap
               (ArchObjectCap $ PageCap dev p R VMVSpaceMap pgsz (Some (asid,vaddr))) cte entries vspace
          odE
    else throwError TruncatedMessage
    else if invocation_type label = ArchInvocationLabel X64PageUnmap then
             \<^cancel>\<open>case map_type of
                 VMIOSpaceMap \<Rightarrow> decode_io_unmap_invocation label args cte cap extra_caps
               | _ \<Rightarrow>\<close> returnOk $ InvokePage $ PageUnmap cap cte
    \<^cancel>\<open>FIXME x64-vtd:
    else if invocation_type label = ArchInvocationLabel X64PageMapIO
    then decode_io_map_invocation label args cte cap extra_caps \<close>
    else if invocation_type label = ArchInvocationLabel X64PageGetAddress
    then returnOk $ InvokePage $ PageGetAddr p
  else throwError IllegalOperation
 | _ \<Rightarrow> fail)"

definition filter_frame_attrs :: "frame_attrs \<Rightarrow> table_attrs"
where
  "filter_frame_attrs attrs \<equiv> {s. \<exists>s' \<in> attrs. s' = PTAttr s}"

definition
  decode_page_table_invocation :: "data \<Rightarrow> data list \<Rightarrow> cslot_ptr \<Rightarrow> arch_cap \<Rightarrow>
                                  (cap \<times> cslot_ptr) list \<Rightarrow> (arch_invocation,'z::state_ext) se_monad"
where
  "decode_page_table_invocation label args cte cap extra_caps \<equiv> (case cap of
    PageTableCap p mapped_address \<Rightarrow>
      if invocation_type label = ArchInvocationLabel X64PageTableMap then
      if length args > 1 \<and> length extra_caps > 0
      then let vaddr = args ! 0;
               attr = args ! 1;
               pml4_cap = fst (extra_caps ! 0)
      in doE
               whenE (mapped_address \<noteq> None) $ throwError $ InvalidCapability 0;
               vaddr' \<leftarrow> returnOk $ vaddr && ~~ mask pd_shift_bits;
               (pml4, asid) \<leftarrow> (case pml4_cap of
                   ArchObjectCap (PML4Cap pml4 (Some asid)) \<Rightarrow> returnOk (pml4, asid)
                   | _ \<Rightarrow> throwError $ InvalidCapability 1);
               whenE (user_vtop < vaddr') $ throwError $ InvalidArgument 0;
               pml4' \<leftarrow> lookup_error_on_failure False $ find_vspace_for_asid asid;
               whenE (pml4' \<noteq> pml4) $ throwError $ InvalidCapability 1;
               pd_slot \<leftarrow> lookup_error_on_failure False $ lookup_pd_slot pml4 vaddr';
               old_pde \<leftarrow> liftE $ get_pde pd_slot;
               unlessE (old_pde = InvalidPDE) $ throwError DeleteFirst;
               pde \<leftarrow> returnOk (PageTablePDE (addrFromPPtr p)
                                  (filter_frame_attrs $ attribs_from_word attr) vm_read_write);
               cap' <- returnOk $ ArchObjectCap $ PageTableCap p $ Some (asid, vaddr');
               returnOk $ InvokePageTable $ PageTableMap cap' cte pde pd_slot pml4
            odE
      else throwError TruncatedMessage
      else if invocation_type label = ArchInvocationLabel X64PageTableUnmap then doE
               final \<leftarrow> liftE $ is_final_cap (ArchObjectCap cap);
               unlessE final $ throwError RevokeFirst;
               returnOk $ InvokePageTable $ PageTableUnmap (ArchObjectCap cap) cte
        odE
      else throwError IllegalOperation
  | _ \<Rightarrow> fail)"

definition
  decode_page_directory_invocation :: "data \<Rightarrow> data list \<Rightarrow> cslot_ptr \<Rightarrow> arch_cap \<Rightarrow>
                                      (cap \<times> cslot_ptr) list \<Rightarrow> (arch_invocation,'z::state_ext) se_monad"
where
  "decode_page_directory_invocation label args cte cap extra_caps \<equiv> (case cap of
    PageDirectoryCap p mapped_address \<Rightarrow>
      if invocation_type label = ArchInvocationLabel X64PageDirectoryMap then
      if length args > 1 \<and> length extra_caps > 0
      then let vaddr = args ! 0;
               attr = args ! 1;
               pml4_cap = fst (extra_caps ! 0)
      in doE
               whenE (mapped_address \<noteq> None) $ throwError $ InvalidCapability 0;
               vaddr' \<leftarrow> returnOk $ vaddr && ~~ mask pdpt_shift_bits;
               (pml4, asid) \<leftarrow> (case pml4_cap of
                       ArchObjectCap (PML4Cap pml4 (Some asid)) \<Rightarrow> returnOk (pml4, asid)
                       | _ \<Rightarrow> throwError $ InvalidCapability 1);
               whenE (user_vtop < vaddr') $ throwError $ InvalidArgument 0;
               pml4' \<leftarrow> lookup_error_on_failure False $ find_vspace_for_asid asid;
               whenE (pml4' \<noteq> pml4) $ throwError $ InvalidCapability 1;
               pdpt_slot \<leftarrow> lookup_error_on_failure False $ lookup_pdpt_slot pml4 vaddr';
               old_pdpte \<leftarrow> liftE $ get_pdpte pdpt_slot;
               unlessE (old_pdpte = InvalidPDPTE) $ throwError DeleteFirst;
               pdpte \<leftarrow> returnOk (PageDirectoryPDPTE (addrFromPPtr p)
                          (filter_frame_attrs $ attribs_from_word attr) vm_read_write);
               cap' <- returnOk $ ArchObjectCap $ PageDirectoryCap p $ Some (asid, vaddr');
               returnOk $ InvokePageDirectory $ PageDirectoryMap cap' cte pdpte pdpt_slot pml4
        odE
      else throwError TruncatedMessage
      else if invocation_type label = ArchInvocationLabel X64PageDirectoryUnmap then doE
               final \<leftarrow> liftE $ is_final_cap (ArchObjectCap cap);
               unlessE final $ throwError RevokeFirst;
               returnOk $ InvokePageDirectory $ PageDirectoryUnmap (ArchObjectCap cap) cte
            odE
      else throwError IllegalOperation
  | _ \<Rightarrow> fail)"

definition
  decode_pdpt_invocation :: "data \<Rightarrow> data list \<Rightarrow> cslot_ptr \<Rightarrow> arch_cap \<Rightarrow>
                                      (cap \<times> cslot_ptr) list \<Rightarrow> (arch_invocation,'z::state_ext) se_monad"
where
  "decode_pdpt_invocation label args cte cap extra_caps \<equiv> (case cap of
    PDPointerTableCap p mapped_address \<Rightarrow>
      if invocation_type label = ArchInvocationLabel X64PDPTMap then
      if length args > 1 \<and> length extra_caps > 0
      then let vaddr = args ! 0;
               attr = args ! 1;
               pml4_cap = fst (extra_caps ! 0)
      in doE
               whenE (mapped_address \<noteq> None) $ throwError $ InvalidCapability 0;
               vaddr' \<leftarrow> returnOk $ vaddr && ~~ mask pml4_shift_bits;
               (pml4 ,asid) \<leftarrow> (case pml4_cap of
                       ArchObjectCap (PML4Cap pml4 (Some asid)) \<Rightarrow> returnOk (pml4, asid)
                       | _ \<Rightarrow> throwError $ InvalidCapability 1);
               whenE (user_vtop < vaddr') $ throwError $ InvalidArgument 0;
               pml4' \<leftarrow> lookup_error_on_failure False $ find_vspace_for_asid (asid);
               whenE (pml4' \<noteq> pml4) $ throwError $ InvalidCapability 1;
               pml_slot \<leftarrow> returnOk $ lookup_pml4_slot pml4 vaddr';
               old_pml4e \<leftarrow> liftE $ get_pml4e pml_slot;
               unlessE (old_pml4e = InvalidPML4E) $ throwError DeleteFirst;
               pml4e \<leftarrow> returnOk (PDPointerTablePML4E (addrFromPPtr p)
                          (filter_frame_attrs $ attribs_from_word attr) vm_read_write);
               cap' <- returnOk $ ArchObjectCap $ PDPointerTableCap p $ Some (asid, vaddr');
               returnOk $ InvokePDPT $ PDPTMap cap' cte pml4e pml_slot pml4
            odE
      else throwError TruncatedMessage
      else if invocation_type label = ArchInvocationLabel X64PDPTUnmap then doE
               final \<leftarrow> liftE $ is_final_cap (ArchObjectCap cap);
               unlessE final $ throwError RevokeFirst;
               returnOk $ InvokePDPT $ PDPTUnmap (ArchObjectCap cap) cte
         odE
      else throwError IllegalOperation
  | _ \<Rightarrow> fail)"

definition
  arch_decode_invocation ::
  "data \<Rightarrow> data list \<Rightarrow> cap_ref \<Rightarrow> cslot_ptr \<Rightarrow> arch_cap \<Rightarrow> (cap \<times> cslot_ptr) list \<Rightarrow>
   (arch_invocation,'z::state_ext) se_monad"
where
  "arch_decode_invocation label args x_slot cte cap extra_caps \<equiv> case cap of
    PDPointerTableCap _ _ \<Rightarrow> decode_pdpt_invocation label args cte cap extra_caps
  | PageDirectoryCap _ _ \<Rightarrow> decode_page_directory_invocation label args cte cap extra_caps
  | PageTableCap _ _ \<Rightarrow> decode_page_table_invocation label args cte cap extra_caps
  | PageCap _ _ _ _ _ _ \<Rightarrow> decode_page_invocation label args cte cap extra_caps
  | ASIDControlCap \<Rightarrow>
      if invocation_type label = ArchInvocationLabel X64ASIDControlMakePool then
      if length args > 1 \<and> length extra_caps > 1
      then let index = args ! 0;
               depth = args ! 1;
               (untyped, parent_slot) = extra_caps ! 0;
               root = fst (extra_caps ! 1)
      in doE
               asid_table \<leftarrow> liftE $ gets (x64_asid_table \<circ> arch_state);
               free_set \<leftarrow> returnOk (- dom asid_table);
               whenE (free_set = {}) $ throwError DeleteFirst;
               free \<leftarrow> liftE $ select_ext (\<lambda>_. free_asid_select asid_table) free_set;
               base \<leftarrow> returnOk (ucast free << asid_low_bits);
               (p,n) \<leftarrow> (case untyped of UntypedCap False p n f \<Rightarrow> returnOk (p,n)
                                        | _ \<Rightarrow> throwError $ InvalidCapability 1);
               frame \<leftarrow> (if n = pageBits
                        then doE
                            ensure_no_children parent_slot;
                            returnOk p
                          odE
                        else  throwError $ InvalidCapability 1);
               dest_slot \<leftarrow> lookup_target_slot root (to_bl index) (unat depth);
               ensure_empty dest_slot;
               returnOk $ InvokeASIDControl $ MakePool frame dest_slot parent_slot base
           odE
   else throwError TruncatedMessage
   else throwError IllegalOperation

  | ASIDPoolCap p base \<Rightarrow>
      if invocation_type label = ArchInvocationLabel X64ASIDPoolAssign then
      if length extra_caps > 0
      then let (pd_cap, pd_cap_slot) = extra_caps ! 0 in
            case pd_cap of
               ArchObjectCap (PML4Cap _ None) \<Rightarrow> doE
                  asid_table \<leftarrow> liftE $ gets (x64_asid_table \<circ> arch_state);
                  pool_ptr \<leftarrow> returnOk (asid_table (asid_high_bits_of base));
                  whenE (pool_ptr = None) $ throwError $ FailedLookup False InvalidRoot;
                  whenE (p \<noteq> the pool_ptr) $ throwError $ InvalidCapability 0;
                  pool \<leftarrow> liftE $ get_asid_pool p;
                  free_set \<leftarrow> returnOk (- dom pool \<inter> {x. ucast x + base \<noteq> 0});
                  whenE (free_set = {}) $ throwError DeleteFirst;
                  offset \<leftarrow> liftE $ select_ext (\<lambda>_. free_asid_pool_select pool base) free_set;
                  returnOk $ InvokeASIDPool $ Assign (ucast offset + base) p pd_cap_slot
              odE
            | _ \<Rightarrow> throwError $ InvalidCapability 1
      else throwError TruncatedMessage
      else throwError IllegalOperation

  | IOPortCap f l \<Rightarrow> decode_port_invocation label args (IOPortCap f l)
  | IOPortControlCap \<Rightarrow> decode_ioport_control_invocation label args cte cap (map fst extra_caps)
  | PML4Cap a b \<Rightarrow> throwError IllegalOperation"

definition
  arch_data_to_obj_type :: "nat \<Rightarrow> aobject_type option" where
 "arch_data_to_obj_type n \<equiv>
  if      n = 0 then Some PDPTObj
  else if n = 1 then Some PML4Obj
  else if n = 2 then Some HugePageObj
  else if n = 3 then Some SmallPageObj
  else if n = 4 then Some LargePageObj
  else if n = 5 then Some PageTableObj
  else if n = 6 then Some PageDirectoryObj
  else None"

definition
  arch_check_irq :: "data \<Rightarrow> (unit,'z::state_ext) se_monad"
where
  "arch_check_irq irq \<equiv> throwError IllegalOperation"

end (* context Arch *)

end

definition perform_asid_control_invocation :: "asid_control_invocation \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "perform_asid_control_invocation iv \<equiv> case iv of
     MakePool frame slot parent base \<Rightarrow> do
       delete_objects frame pageBits;
       pcap \<leftarrow> get_cap parent;
       set_cap (max_free_index_update pcap) parent;
       retype_region frame 1 0 (ArchObject ASIDPoolObj) False;
       cap_insert (ArchObjectCap $ ASIDPoolCap frame base) parent slot;
       assert (asid_low_bits_of base = 0);
       asid_table \<leftarrow> gets (riscv_asid_table \<circ> arch_state);
       asid_table' \<leftarrow> return (asid_table (asid_high_bits_of base \<mapsto> frame));
       modify (\<lambda>s. s \<lparr>arch_state := (arch_state s) \<lparr>riscv_asid_table := asid_table'\<rparr>\<rparr>)
     od"

definition perform_asid_pool_invocation :: "asid_pool_invocation \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "perform_asid_pool_invocation iv \<equiv> case iv of
     Assign asid pool_ptr ct_slot \<Rightarrow> do
       pt_cap \<leftarrow> get_cap ct_slot;
       assert $ is_ArchObjectCap pt_cap;
       acap \<leftarrow> return $ the_arch_cap pt_cap;
       assert $ is_PageTableCap acap;
       set_cap (ArchObjectCap $ update_map_data acap $ Some (asid,0)) ct_slot;
       pt_base \<leftarrow> return $ acap_obj acap;
       copy_global_mappings pt_base;
       store_asid_pool_entry pool_ptr asid (Some pt_base)
     od"

definition perform_pg_inv_unmap :: "arch_cap \<Rightarrow> cslot_ptr \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "perform_pg_inv_unmap cap ct_slot \<equiv> do
     assert $ is_FrameCap cap;
     case acap_map_data cap of
       Some (asid, vaddr) \<Rightarrow> unmap_page (acap_fsize cap) asid vaddr (acap_obj cap)
     | _ \<Rightarrow> return ();
     old_cap \<leftarrow> get_cap ct_slot;
     set_cap (ArchObjectCap $ update_map_data (the_arch_cap old_cap) None) ct_slot
   od"

definition perform_pg_inv_map :: "arch_cap \<Rightarrow> cslot_ptr \<Rightarrow> pte \<Rightarrow> obj_ref \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "perform_pg_inv_map cap ct_slot pte slot \<equiv> do
     set_cap (ArchObjectCap cap) ct_slot;
     store_pte slot pte;
     do_machine_op sfence
   od"

definition perform_pg_inv_get_addr :: "obj_ref \<Rightarrow> (data list,'z::state_ext) s_monad"
  where
  "perform_pg_inv_get_addr ptr \<equiv> return [addrFromPPtr ptr]"

definition lookup_ipc_buffer :: "bool \<Rightarrow> obj_ref \<Rightarrow> (obj_ref option,'z::state_ext) s_monad"
  where
  "lookup_ipc_buffer is_receiver thread \<equiv> do
     buffer_ptr \<leftarrow> thread_get tcb_ipc_buffer thread;
     buffer_frame_slot \<leftarrow> return (thread, tcb_cnode_index 4);
     buffer_cap \<leftarrow> get_cap buffer_frame_slot;
     case buffer_cap of
       ArchObjectCap (FrameCap p R vms False _) \<Rightarrow>
         if vm_read_write \<subseteq> R \<or> vm_read_only \<subseteq> R \<and> \<not>is_receiver
         then return $ Some $ p + (buffer_ptr && mask (pageBitsForSize vms))
         else return None
     | _ \<Rightarrow> return None
   od"

definition pool_for_asid :: "asid \<Rightarrow> 'z::state_ext state \<Rightarrow> obj_ref option"
  where
  "pool_for_asid asid \<equiv> \<lambda>s. riscv_asid_table (arch_state s) (asid_high_bits_of asid)"

definition vspace_for_pool :: "obj_ref \<Rightarrow> asid \<Rightarrow> (obj_ref \<rightharpoonup> asid_pool) \<Rightarrow> obj_ref option"
  where
  "vspace_for_pool pool_ptr asid \<equiv> do {
     pool \<leftarrow> oapply pool_ptr;
     K $ pool (asid_low_bits_of asid)
   }"

definition vspace_for_asid :: "asid \<Rightarrow> 'z::state_ext state \<Rightarrow> obj_ref option"
  where
  "vspace_for_asid asid = do {
     oassert (0 < asid);
     pool_ptr \<leftarrow> pool_for_asid asid;
     vspace_for_pool pool_ptr asid \<circ> asid_pools_of
   }"

definition set_vm_root :: "obj_ref \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "set_vm_root tcb \<equiv> do
    thread_root_slot \<leftarrow> return (tcb, tcb_cnode_index 1);
    thread_root \<leftarrow> get_cap thread_root_slot;
    (case thread_root of
       ArchObjectCap (PageTableCap pt (Some (asid, _))) \<Rightarrow> doE
           pt' \<leftarrow> find_vspace_for_asid asid;
           whenE (pt \<noteq> pt') $ throwError InvalidRoot;
           liftE $ do_machine_op $ setVSpaceRoot (addrFromPPtr pt) (ucast asid)
       odE
     | _ \<Rightarrow> throwError InvalidRoot) <catch>
    (\<lambda>_. do
       global_pt \<leftarrow> gets global_pt;
       do_machine_op $ setVSpaceRoot (addrFromKPPtr global_pt) 0
    od)
  od"


definition delete_asid_pool :: "asid \<Rightarrow> obj_ref \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "delete_asid_pool base ptr \<equiv> do
     assert (asid_low_bits_of base = 0);
     asid_table \<leftarrow> gets (riscv_asid_table \<circ> arch_state);
     when (asid_table (asid_high_bits_of base) = Some ptr) $ do
       pool \<leftarrow> get_asid_pool ptr;
       asid_table' \<leftarrow> return $ asid_table (asid_high_bits_of base:= None);
       modify (\<lambda>s. s \<lparr> arch_state := (arch_state s) \<lparr> riscv_asid_table := asid_table' \<rparr>\<rparr>);
       tcb \<leftarrow> gets cur_thread;
       set_vm_root tcb
     od
   od"


definition delete_asid :: "asid \<Rightarrow> obj_ref \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "delete_asid asid pt \<equiv> do
     asid_table \<leftarrow> gets (riscv_asid_table \<circ> arch_state);
     case asid_table (asid_high_bits_of asid) of
       None \<Rightarrow> return ()
     | Some pool_ptr \<Rightarrow> do
         pool \<leftarrow> get_asid_pool pool_ptr;
         when (pool (asid_low_bits_of asid) = Some pt) $ do
           do_machine_op $ hwASIDFlush (ucast asid);
           pool' \<leftarrow> return $ pool (asid_low_bits_of asid := None);
           set_asid_pool pool_ptr pool';
           tcb \<leftarrow> gets cur_thread;
           set_vm_root tcb
         od
       od
   od"

definition unmap_page_table :: "asid \<Rightarrow> vspace_ref \<Rightarrow> obj_ref \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "unmap_page_table asid vaddr pt \<equiv> doE
     top_level_pt \<leftarrow> find_vspace_for_asid asid;
     pt_slot \<leftarrow> pt_lookup_from_level max_pt_level top_level_pt vaddr pt;
     liftE $ store_pte pt_slot InvalidPTE;
     liftE $ do_machine_op sfence
   odE <catch> (K $ return ())"

definition vs_lookup_table :: "vm_level \<Rightarrow> asid \<Rightarrow> vspace_ref \<Rightarrow> 'z::state_ext state \<Rightarrow> (vm_level \<times> obj_ref) option"
  where
  "vs_lookup_table bot_level asid vptr \<equiv> do {
     pool_ptr \<leftarrow> pool_for_asid asid;
     if bot_level = asid_pool_level
     then oreturn (asid_pool_level, pool_ptr)
     else do {
       top_level_pt \<leftarrow> vspace_for_pool pool_ptr asid \<circ> asid_pools_of;
       pt_walk max_pt_level bot_level top_level_pt vptr \<circ> ptes_of
     }
   }"

definition vs_lookup_slot :: "vm_level \<Rightarrow> asid \<Rightarrow> vspace_ref \<Rightarrow> 'z::state_ext state \<Rightarrow> (vm_level \<times> obj_ref) option"
  where
  "vs_lookup_slot bot_level asid vref \<equiv> do {
     (level', table) \<leftarrow> vs_lookup_table bot_level asid vref;
     if level' = asid_pool_level then
       oreturn (level', table)
     else
       oreturn (level', pt_slot_offset level' table vref)
   }"

definition unmap_page :: "vmpage_size \<Rightarrow> asid \<Rightarrow> vspace_ref \<Rightarrow> obj_ref \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "unmap_page pgsz asid vptr pptr \<equiv> doE
     top_level_pt \<leftarrow> find_vspace_for_asid asid;
     (lev, slot) \<leftarrow> liftE $ gets_the $ pt_lookup_slot top_level_pt vptr \<circ> ptes_of;
     unlessE (pt_bits_left lev = pageBitsForSize pgsz) $ throwError InvalidRoot;
     pte \<leftarrow> liftE $ get_pte slot;
     unlessE (is_PagePTE pte \<and> pptr_from_pte pte = pptr) $ throwError InvalidRoot;
     liftE $ store_pte slot InvalidPTE;
     liftE $ do_machine_op sfence
   odE <catch> (K $ return ())"

definition arch_finalise_cap :: "arch_cap \<Rightarrow> bool \<Rightarrow> (cap \<times> cap,'z::state_ext) s_monad"
  where
  "arch_finalise_cap c x \<equiv> case (c, x) of
     (ASIDPoolCap ptr b, True) \<Rightarrow>  do
       delete_asid_pool b ptr;
       return (NullCap, NullCap)
     od
   | (PageTableCap ptr (Some (a, v)), True) \<Rightarrow> do
       doE
         vroot \<leftarrow> find_vspace_for_asid a;
         if vroot = ptr then liftE $ delete_asid a ptr else throwError InvalidRoot
       odE <catch>
       (\<lambda>_. unmap_page_table a v ptr);
       return (NullCap, NullCap)
     od
   | (FrameCap ptr _ sz _ (Some (a, v)), _) \<Rightarrow> do
       unmap_page sz a v ptr;
       return (NullCap, NullCap)
     od
   | _ \<Rightarrow> return (NullCap, NullCap)"

definition pte_of :: "obj_ref \<Rightarrow> (obj_ref \<rightharpoonup> pt) \<rightharpoonup> pte"
  where
  "pte_of p \<equiv> do {
     oassert (is_aligned p pte_bits);
     pt \<leftarrow> oapply (table_base p);
     oreturn $ pt (table_index p)
   }"

locale_abbrev ptes_of :: "'z::state_ext state \<Rightarrow> obj_ref \<rightharpoonup> pte"
  where
  "ptes_of s \<equiv> \<lambda>p. pte_of p (pts_of s)"

locale_abbrev get_pte :: "obj_ref \<Rightarrow> (pte,'z::state_ext) s_monad"
  where
  "get_pte \<equiv> gets_map ptes_of"

definition store_pte :: "obj_ref \<Rightarrow> pte \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "store_pte p pte \<equiv> do
     assert (is_aligned p pte_bits);
     base \<leftarrow> return $ table_base p;
     index \<leftarrow> return $ table_index p;
     pt \<leftarrow> get_pt (table_base p);
     pt' \<leftarrow> return $ pt (index := pte);
     set_pt base pt'
   od"

definition copy_global_mappings :: "obj_ref \<Rightarrow> (unit,'z::state_ext) s_monad"
  where
  "copy_global_mappings new_pm \<equiv> do
    global_pt \<leftarrow> gets global_pt;
    base \<leftarrow> return $ pt_index max_pt_level pptr_base;
    pt_size \<leftarrow> return $ 1 << ptTranslationBits;
    mapM_x (\<lambda>index. do
        offset \<leftarrow> return (index << pte_bits);
        pme \<leftarrow> get_pte (global_pt + offset);
        store_pte (new_pm + offset) pme
    od) [base  .e.  pt_size - 1]
  od"

comment:
The kernel window is mapped into every virtual address space from the @{term pptr_base}
pointer upwards. This function copies the mappings which create the kernel window into a new
top-level page table object.

spec:
fun pt_walk ::
  "vm_level \<Rightarrow> vm_level \<Rightarrow> obj_ref \<Rightarrow> vspace_ref \<Rightarrow> (obj_ref \<rightharpoonup> pte) \<Rightarrow> (vm_level \<times> obj_ref) option"
  where
  "pt_walk level bot_level pt_ptr vptr = do {
     if bot_level < level
     then do {
       pte \<leftarrow> oapply (pt_slot_offset level pt_ptr vptr);
       if is_PageTablePTE pte
         then pt_walk (level - 1) bot_level (pptr_from_pte pte) vptr
         else oreturn (level, pt_ptr)
     }
     else oreturn (level, pt_ptr)
   }"

declare pt_walk.simps[simp del]

definition pt_lookup_slot_from_level ::
  "vm_level \<Rightarrow> vm_level \<Rightarrow> obj_ref \<Rightarrow> vspace_ref \<Rightarrow> (obj_ref \<rightharpoonup> pte) \<Rightarrow> (vm_level \<times> obj_ref) option"
  where
  "pt_lookup_slot_from_level level bot_level pt_ptr vptr = do {
     (level', pt_ptr') \<leftarrow> pt_walk level bot_level pt_ptr vptr;
     oreturn (level', pt_slot_offset level' pt_ptr' vptr)
   }"

definition pt_lookup_slot :: "obj_ref \<Rightarrow> vspace_ref \<Rightarrow> (obj_ref \<rightharpoonup> pte) \<Rightarrow> (vm_level \<times> obj_ref) option"
  where
  "pt_lookup_slot = pt_lookup_slot_from_level max_pt_level 0"

fun pt_lookup_from_level ::
  "vm_level \<Rightarrow> obj_ref \<Rightarrow> vspace_ref \<Rightarrow> obj_ref \<Rightarrow> (machine_word, 'z::state_ext) lf_monad"
  where
  "pt_lookup_from_level level pt_ptr vptr target_pt_ptr s = (doE
     unlessE (0 < level) $ throwError InvalidRoot;
     slot <- returnOk $ pt_slot_offset level pt_ptr vptr;
     pte <- liftE $ gets_the $ oapply slot o ptes_of;
     unlessE (is_PageTablePTE pte) $ throwError InvalidRoot;
     ptr <- returnOk (pptr_from_pte pte);
     if ptr = target_pt_ptr
       then returnOk slot
       else pt_lookup_from_level (level - 1) ptr vptr target_pt_ptr
   odE) s"

definition
  arch_check_irq :: "data \<Rightarrow> (unit,'z::state_ext) se_monad"
where
  "arch_check_irq irq \<equiv> whenE (irq > ucast maxIRQ \<or> irq = ucast irqInvalid) $
                          throwError (RangeError 1 (ucast maxIRQ))"

definition arch_decode_irq_control_invocation ::
  "data \<Rightarrow> data list \<Rightarrow> cslot_ptr \<Rightarrow> cap list \<Rightarrow> (arch_irq_control_invocation,'z::state_ext) se_monad"
  where
  "arch_decode_irq_control_invocation label args src_slot cps \<equiv>
     (if invocation_type label = ArchInvocationLabel RISCVIRQIssueIRQHandler
      then if length args \<ge> 4 \<and> length cps \<ge> 1
        then let irq_word = args ! 0;
                 trigger = args ! 1;
                 index = args ! 2;
                 depth = args ! 3;
                 cnode = cps ! 0;
                 irq = ucast irq_word
        in doE
          arch_check_irq irq_word;
          irq_active \<leftarrow> liftE $ is_irq_active irq;
          whenE irq_active $ throwError RevokeFirst;
          dest_slot \<leftarrow> lookup_target_slot cnode (data_to_cptr index) (unat depth);
          ensure_empty dest_slot;
          returnOk $ RISCVIRQControlInvocation irq dest_slot src_slot (trigger \<noteq> 0)
        odE
      else throwError TruncatedMessage
    else throwError IllegalOperation)"

definition attribs_from_word :: "machine_word \<Rightarrow> vm_attributes"
  where
  "attribs_from_word w \<equiv> if \<not> w!!0 then {Execute} else {}"

definition make_user_pte :: "vspace_ref \<Rightarrow> vm_attributes \<Rightarrow> vm_rights \<Rightarrow> pte"
  where
  "make_user_pte addr attr rights \<equiv>
    if rights = {} \<and> attr = {}
    then InvalidPTE
    else PagePTE (ucast (addr >> pageBits)) (attr \<union> {User}) rights"

definition check_slot :: "obj_ref \<Rightarrow> (pte \<Rightarrow> bool) \<Rightarrow> (unit,'z::state_ext) se_monad"
  where
  "check_slot slot test = doE
     pte \<leftarrow> liftE $ get_pte slot;
     unlessE (test pte) $ throwError DeleteFirst
   odE"

type_synonym 'z arch_decoder =
  "data \<Rightarrow> data list \<Rightarrow> cslot_ptr \<Rightarrow> arch_cap \<Rightarrow> (cap \<times> cslot_ptr) list \<Rightarrow>
    (arch_invocation,'z) se_monad"

definition decode_fr_inv_map :: "'z::state_ext arch_decoder"
  where
  "decode_fr_inv_map label args cte cap extra_caps \<equiv> case cap of
     FrameCap p R pgsz dev mapped_address \<Rightarrow>
       if length args > 2 \<and> length extra_caps > 0
       then let
           vaddr = args ! 0;
           rights_mask = args ! 1;
           attr = args ! 2;
           vspace_cap = fst (extra_caps ! 0)
         in doE
           (pt, asid) \<leftarrow> case vspace_cap of
                           ArchObjectCap (PageTableCap pt (Some (asid, _))) \<Rightarrow> returnOk (pt, asid)
                         | _ \<Rightarrow> throwError $ InvalidCapability 1;
           pt' \<leftarrow> lookup_error_on_failure False $ find_vspace_for_asid asid;
           whenE (pt' \<noteq> pt) $ throwError $ InvalidCapability 1;
           pg_bits \<leftarrow> returnOk $ pageBitsForSize pgsz;
           vtop \<leftarrow> returnOk $ vaddr + mask (pageBitsForSize pgsz);
           whenE (vtop \<ge> user_vtop) $ throwError $ InvalidArgument 0;
           check_vp_alignment pgsz vaddr;
           (level, slot) \<leftarrow> liftE $ gets_the $ pt_lookup_slot pt vaddr \<circ> ptes_of;
           unlessE (pt_bits_left level = pg_bits) $
             throwError $ FailedLookup False $ MissingCapability $ pt_bits_left level;
           case mapped_address of
             Some (asid', vaddr') \<Rightarrow> doE
               whenE (asid' \<noteq> asid) (throwError $ InvalidCapability 1);
               whenE (vaddr' \<noteq> vaddr) (throwError $ InvalidArgument 0);
               check_slot slot (Not \<circ> is_PageTablePTE)
             odE
           | None \<Rightarrow> check_slot slot ((=) InvalidPTE);
           vm_rights \<leftarrow> returnOk $ mask_vm_rights R (data_to_rights rights_mask);
           attribs \<leftarrow> returnOk $ attribs_from_word attr;
           pte \<leftarrow> returnOk $ make_user_pte (addrFromPPtr p) attribs vm_rights;
           returnOk $ InvokePage $ PageMap (FrameCap p R pgsz dev (Some (asid,vaddr))) cte (pte,slot)
         odE
       else throwError TruncatedMessage
     | _ \<Rightarrow> fail"

definition decode_frame_invocation :: "'z::state_ext arch_decoder"
  where
  "decode_frame_invocation label args cte cap extra_caps \<equiv>
     if invocation_type label = ArchInvocationLabel RISCVPageMap
     then decode_fr_inv_map label args cte cap extra_caps
     else if invocation_type label = ArchInvocationLabel RISCVPageUnmap
     then returnOk $ InvokePage $ PageUnmap cap cte
     else if invocation_type label = ArchInvocationLabel RISCVPageGetAddress
     then returnOk $ InvokePage $ PageGetAddr (acap_obj cap)
     else throwError IllegalOperation"

definition decode_pt_inv_map :: "'z::state_ext arch_decoder"
  where
  "decode_pt_inv_map label args cte cap extra_caps \<equiv> case cap of
     PageTableCap p mapped_address \<Rightarrow>
       if length args > 1 \<and> length extra_caps > 0
       then let
           vaddr = args ! 0;
           attr = args ! 1;
           vspace_cap = fst (extra_caps ! 0)
         in doE
           whenE (mapped_address \<noteq> None) $ throwError $ InvalidCapability 0;
           (pt, asid) \<leftarrow> case vspace_cap of
                           ArchObjectCap (PageTableCap pt (Some (asid,_))) \<Rightarrow> returnOk (pt, asid)
                         | _ \<Rightarrow> throwError $ InvalidCapability 1;
           whenE (user_vtop \<le> vaddr) $ throwError $ InvalidArgument 0;
           pt' \<leftarrow> lookup_error_on_failure False $ find_vspace_for_asid asid;
           whenE (pt' \<noteq> pt) $ throwError $ InvalidCapability 1;
           (level, slot) \<leftarrow> liftE $ gets_the $ pt_lookup_slot pt vaddr \<circ> ptes_of;
           old_pte \<leftarrow> liftE $ get_pte slot;
           whenE (pt_bits_left level = pageBits \<or> old_pte \<noteq> InvalidPTE) $ throwError DeleteFirst;
           pte \<leftarrow> returnOk $ PageTablePTE (ucast (addrFromPPtr p >> pageBits)) {};
           cap' <- returnOk $ PageTableCap p $ Some (asid, vaddr && ~~mask (pt_bits_left level));
           returnOk $ InvokePageTable $ PageTableMap cap' cte pte slot
         odE
       else throwError TruncatedMessage
     | _ \<Rightarrow> fail"

definition decode_page_table_invocation :: "'z::state_ext arch_decoder"
  where
  "decode_page_table_invocation label args cte cap extra_caps \<equiv>
     if invocation_type label = ArchInvocationLabel RISCVPageTableMap
     then decode_pt_inv_map label args cte cap extra_caps
     else if invocation_type label = ArchInvocationLabel RISCVPageTableUnmap
     then doE
       final \<leftarrow> liftE $ is_final_cap (ArchObjectCap cap);
       unlessE final $ throwError RevokeFirst;
       case cap of
         PageTableCap pt (Some (asid, _)) \<Rightarrow> doE
             \<comment> \<open>cannot invoke unmap on top level page table\<close>
             pt_opt \<leftarrow> liftE $ gets $ vspace_for_asid asid;
             whenE (pt_opt = Some pt) $ throwError RevokeFirst
           odE
       | _ \<Rightarrow> returnOk ();
       returnOk $ InvokePageTable $ PageTableUnmap cap cte
     odE
     else throwError IllegalOperation"

definition decode_asid_control_invocation :: "'z::state_ext arch_decoder"
  where
  "decode_asid_control_invocation label args cte cap extra_caps \<equiv>
     if invocation_type label = ArchInvocationLabel RISCVASIDControlMakePool
     then if length args > 1 \<and> length extra_caps > 1
     then let
         index = args ! 0;
         depth = args ! 1;
         (untyped, parent_slot) = extra_caps ! 0;
         root = fst (extra_caps ! 1)
       in doE
         asid_table \<leftarrow> liftE $ gets (riscv_asid_table \<circ> arch_state);
         free_set \<leftarrow> returnOk (- dom asid_table);
         whenE (free_set = {}) $ throwError DeleteFirst;
         free \<leftarrow> liftE $ select_ext (\<lambda>_. free_asid_select asid_table) free_set;
         base \<leftarrow> returnOk (ucast free << asid_low_bits);
         (p,n) \<leftarrow> case untyped of
                    UntypedCap False p n _ \<Rightarrow> returnOk (p,n)
                  | _ \<Rightarrow> throwError $ InvalidCapability 1;
         frame \<leftarrow> if n = pageBits then doE
                    ensure_no_children parent_slot;
                    returnOk p
                  odE
                  else throwError $ InvalidCapability 1;
         dest_slot \<leftarrow> lookup_target_slot root (to_bl index) (unat depth);
         ensure_empty dest_slot;
         returnOk $ InvokeASIDControl $ MakePool frame dest_slot parent_slot base
       odE
     else throwError TruncatedMessage
     else throwError IllegalOperation"

definition decode_asid_pool_invocation :: "'z::state_ext arch_decoder"
  where
  "decode_asid_pool_invocation label args cte cap extra_caps \<equiv>
     if invocation_type label = ArchInvocationLabel RISCVASIDPoolAssign
     then if length extra_caps > 0
     then let
         (pt_cap, pt_cap_slot) = extra_caps ! 0;
         p = acap_obj cap;
         base = acap_asid_base cap
       in case pt_cap of
         ArchObjectCap (PageTableCap _ None) \<Rightarrow> doE
           asid_table \<leftarrow> liftE $ gets (riscv_asid_table \<circ> arch_state);
           pool_ptr \<leftarrow> returnOk (asid_table (asid_high_bits_of base));
           whenE (pool_ptr = None) $ throwError $ FailedLookup False InvalidRoot;
           whenE (p \<noteq> the pool_ptr) $ throwError $ InvalidCapability 0;
           pool \<leftarrow> liftE $ get_asid_pool p;
           free_set \<leftarrow> returnOk (- dom pool \<inter> {x. ucast x + base \<noteq> 0});
           whenE (free_set = {}) $ throwError DeleteFirst;
           offset \<leftarrow> liftE $ select_ext (\<lambda>_. free_asid_pool_select pool base) free_set;
           returnOk $ InvokeASIDPool $ Assign (ucast offset + base) p pt_cap_slot
         odE
       | _ \<Rightarrow> throwError $ InvalidCapability 1
     else throwError TruncatedMessage
     else throwError IllegalOperation"

definition arch_decode_invocation ::
  "data \<Rightarrow> data list \<Rightarrow> cap_ref \<Rightarrow> cslot_ptr \<Rightarrow> arch_cap \<Rightarrow> (cap \<times> cslot_ptr) list \<Rightarrow>
    (arch_invocation,'z::state_ext) se_monad"
  where
  "arch_decode_invocation label args x_slot cte cap extra_caps \<equiv> case cap of
     PageTableCap _ _   \<Rightarrow> decode_page_table_invocation label args cte cap extra_caps
   | FrameCap _ _ _ _ _ \<Rightarrow> decode_frame_invocation label args cte cap extra_caps
   | ASIDControlCap     \<Rightarrow> decode_asid_control_invocation label args cte cap extra_caps
   | ASIDPoolCap _ _    \<Rightarrow> decode_asid_pool_invocation label args cte cap extra_caps"

definition make_virq :: "obj_ref \<Rightarrow> obj_ref \<Rightarrow> obj_ref \<Rightarrow> virq" where
  "make_virq grp prio irq \<equiv>
    let
      groupShift = 30;
      prioShift = 23;
      irqPending = 1 << 28;
      eoiirqen = 1 << 19
    in ((grp && 1) << groupShift) || ((prio && 0x1F) << prioShift) || (irq && 0x3FF)
       || irqPending || eoiirqen"

definition virq_type :: "virq \<Rightarrow> nat" where
  "virq_type virq \<equiv> unat ((virq >> 28) && 3)"

definition is_virq_active :: "virq \<Rightarrow> bool" where
  "is_virq_active virq \<equiv> virq_type virq = 2"

definition decode_vcpu_inject_irq ::
  "obj_ref list \<Rightarrow> arch_cap \<Rightarrow> (arch_invocation,'z::state_ext) se_monad"
  where
  "decode_vcpu_inject_irq ptrs cap \<equiv> case (ptrs, cap) of
  (mr0 # _, VCPUCap p) \<Rightarrow> doE
     vid \<leftarrow> returnOk (mr0 && 0xFFFF);
     priority \<leftarrow> returnOk ((mr0 >> 16) && 0xFF);
     group \<leftarrow> returnOk ((mr0 >> 24) && 0xFF);
     index \<leftarrow> returnOk ((mr0 >> 32) && 0xFF);
     range_check vid 0 ((1 << 10) - 1);
     range_check priority 0 31;
     range_check group 0 1;
     num_list_regs \<leftarrow> liftE $ gets (arm_gicvcpu_numlistregs \<circ> arch_state);
     whenE (index \<ge> of_nat num_list_regs) $
        (throwError $ RangeError 0 (of_nat num_list_regs - 1));

     vcpu \<leftarrow> liftE $ get_vcpu p;
     vcpuLR \<leftarrow> returnOk (vgic_lr $ vcpu_vgic $ vcpu);

     whenE (is_virq_active (vcpuLR (unat index))) $ throwError DeleteFirst;

     virq \<leftarrow> returnOk (make_virq group priority vid);
     returnOk $ InvokeVCPU $ VCPUInjectIRQ p (unat index) virq
  odE
| _ \<Rightarrow> throwError TruncatedMessage"

definition invoke_vcpu_inject_irq :: "obj_ref \<Rightarrow> nat \<Rightarrow> virq \<Rightarrow> (unit,'z::state_ext) s_monad" where
  "invoke_vcpu_inject_irq vr index virq \<equiv> do
    cur_v \<leftarrow> gets (arm_current_vcpu \<circ> arch_state);
    if (cur_v \<noteq> None \<and> fst (the cur_v) = vr)
    then do_machine_op $ set_gic_vcpu_ctrl_lr (of_nat index) virq
    else vgic_update_lr vr index virq
   od"

chapter "Arch-specific Interrupts"

theory ArchInterrupt_A
imports Ipc_A
begin

context Arch begin arch_global_naming (A)

definition virqSetEOIIRQEN :: "virq \<Rightarrow> machine_word \<Rightarrow> virq" where
  "virqSetEOIIRQEN virq v \<equiv>
     if virq_type virq = 3
     then virq
     else (virq && ~~0x80000) || ((v << 19) && 0x80000)"

definition vgic_maintenance :: "(unit,'z::state_ext) s_monad" where
  "vgic_maintenance = do
     cur_vcpu \<leftarrow> gets (arm_current_vcpu \<circ> arch_state);
     case cur_vcpu
       of Some (vcpu_ptr, True) \<Rightarrow> do
            eisr0 \<leftarrow> do_machine_op $ get_gic_vcpu_ctrl_eisr0;
            eisr1 \<leftarrow> do_machine_op $ get_gic_vcpu_ctrl_eisr1;
            flags \<leftarrow> do_machine_op $ get_gic_vcpu_ctrl_misr;
            vgic_misr_eoi \<leftarrow> return $ vgicHCREN;
            irq_idx \<leftarrow> return (if eisr0 \<noteq> 0 then word_ctz eisr0 else word_ctz eisr1 + 32);
            gic_vcpu_num_list_regs \<leftarrow> gets (arm_gicvcpu_numlistregs o arch_state);
            fault \<leftarrow> if flags && vgic_misr_eoi \<noteq> 0
                    then
                      if eisr0 = 0 \<and> eisr1 = 0 \<or> irq_idx \<ge> gic_vcpu_num_list_regs
                      then return $ VGICMaintenance None
                      else do
                        virq <- do_machine_op $ get_gic_vcpu_ctrl_lr (of_nat irq_idx);
                        virqen <- return $ virqSetEOIIRQEN virq 0;
                        do_machine_op $ set_gic_vcpu_ctrl_lr (of_nat irq_idx) virqen;
                        vgic_update_lr vcpu_ptr irq_idx virqen;
                        return $ VGICMaintenance $ Some $ of_nat irq_idx
                      od
                    else return $ VGICMaintenance None;

            ct \<leftarrow> gets cur_thread;
            st \<leftarrow> get_thread_state ct;
            \<comment> \<open>until a proof links active current vcpu to runnable current thread, we need this
               check: @{term handle_fault} needs a runnable current thread\<close>
            when (runnable st) $ handle_fault ct $ ArchFault fault
          od
        | _ \<Rightarrow> return ()
   od"

definition vppi_event :: "irq \<Rightarrow> (unit,'z::state_ext) s_monad" where
  "vppi_event irq = do
     cur_vcpu \<leftarrow> gets (arm_current_vcpu \<circ> arch_state);
     case cur_vcpu
       of Some (vcpu_ptr, True) \<Rightarrow> do
            do_machine_op $ maskInterrupt True irq;
            vppi \<leftarrow> return $ the $ irq_vppi_event_index irq;
            vcpu_update vcpu_ptr
                        (\<lambda>vcpu. vcpu\<lparr> vcpu_vppi_masked := (vcpu_vppi_masked vcpu)(vppi := True) \<rparr>);
            ct \<leftarrow> gets cur_thread;
            st \<leftarrow> get_thread_state ct;
            \<comment> \<open>until a proof links active current vcpu to runnable current thread, we need this
               check: @{term handle_fault} needs a runnable current thread\<close>
            when (runnable st) $ handle_fault ct $ ArchFault $ VPPIEvent irq
          od
        | _ \<Rightarrow> return ()
   od"

definition handle_reserved_irq :: "irq \<Rightarrow> (unit,'z::state_ext) s_monad" where
  "handle_reserved_irq irq \<equiv> do
     when (irq = irqVGICMaintenance) vgic_maintenance;
     when (irq_vppi_event_index irq \<noteq> None) $ vppi_event irq
   od"

fun arch_invoke_irq_handler :: "irq_handler_invocation \<Rightarrow> (unit,'z::state_ext) s_monad" where
  "arch_invoke_irq_handler (ACKIrq irq) = (do_machine_op $ maskInterrupt False irq)"
| "arch_invoke_irq_handler _ = return ()"

definition arch_mask_irq_signal :: "irq \<Rightarrow> (unit,'z::state_ext) s_monad" where
  "arch_mask_irq_signal irq \<equiv> do_machine_op $ maskInterrupt True irq"

